import React from 'react'
import { useNavigate } from 'react-router-dom';
import { useRef, useState } from 'react';
import axios from 'axios';

function Content() {

    const [info, setInfo] = useState({
        name : '',
        title :'',
        date : '',
        content : '',
        pw : ''
    });
    //로딩시 내용뿌려줌
    useEffect(() => {
        getContent()
    }, [])

    const getContent = () => {

        const data = {
            uid: uid
        }
        axios.post('http://localhost:3001/view', data).then((res) => {
            if (res.data.result == 'success') {
                setInfo({
                    name : res.data.row[0].name,
                    title :res.data.row[0].title,
                    date : res.data.row[0].regdate,
                    content : res.data.row[0].content,
                    pw : res.data.row[0].name
                })
            }
        });

        return (
            <>
                <section className="notice">
                    <div className="page-title">
                        <div className="container">
                            <h3>글쓰기</h3>
                        </div>
                    </div>
                    <div className="container">
                        <table class="table">
                            <tr>
                                <td>작성자</td>
                                <td>
                                    {info.name}
                                </td>
                            </tr>

                            <tr>
                                <td>제목</td>
                                <td>
                                {info.title}
                                </td>
                            </tr>

                            <tr>
                                <td>내용</td>
                                <td>
                                {info.content}
                                </td>
                            </tr>

                            <tr>
                                <td>비밀번호</td>
                                <td>
                                {info.pw}
                                </td>
                            </tr>
                        </table>

                        <div className="foot_area">
                            <input type="button"  className="btn btn-dark reg" value="수정하기" />
                            <input type="button" onClick={() => nav(-1)} className="btn btn-dark back" value="뒤로가기" />
                        </div>

                    </div >
                </section >
            </>
        )

    }
}

export default Content;
