import {Link, useNavigate } from "react-router-dom";
import React from 'react'
import { useRef, useState,useEffect } from 'react';
import axios from 'axios';

function BoardList() {

    const [boardList, setBoardList] = useState([]);

    useEffect(()=>{
        list()
    },[])

    const list = () => {
        axios.post('http://localhost:3001/list').then((res) => {
            if (res.data.result == 'success') {
                arrBoardList(res.data.row);
            }
        });
    }

    const arrBoardList = (data) => {
        let cnt = 1;
        let device = data.map((val, index) => {
            const date = new Date(val.regdate)
            return (
                <>
                    <tr>
                        <td>{cnt}</td>
                        <th>
                            <a href="#!">{val.title}</a>
                        </th>
                        <td>{val.name}</td>
                        <td>{date}</td>
                        <td>{val.hit}</td>
                    </tr>
                </>
            )
            cnt++;
        })

        setBoardList(device);
    }

    return (
        <>
            <section className="notice">
                <div className="page-title">
                    <div className="container">
                        <h3>익명 게시판</h3>
                    </div>
                </div>

                <div id="board-search">
                    <div className="container">
                        <div className="search-window">
                            <form action="" className='search_form'>
                                <div className="search-wrap">
                                    <label for="search" className="blind">공지사항 내용 검색</label>
                                    <input id="search" type="search" name="" placeholder="검색어를 입력해주세요." value="" />
                                    <button type="submit" className="btn btn-dark">검색</button>
                                </div>
                                <Link to='/write' className="btn btn-dark write">글쓰기</Link >
                            </form>
                        </div>
                    </div>
                </div>

                <div id="board-list">
                    <div className="container">
                        <table className="board-table">
                            <thead>
                                <tr>
                                    <th scope="col" className="th-num">번호</th>
                                    <th scope="col" className="th-title">제목</th>
                                    <th scope="col" className="th-name">작성자</th>
                                    <th scope="col" className="th-date">등록일</th>
                                    <th scope="col" className="th-hit">조회수</th>
                                </tr>
                            </thead>
                            <tbody>
                                {boardList}
                            </tbody>
                        </table>
                    </div>
                </div>

                <div id="divPaging">
                    <div>◀</div>
                    <div><b>1</b></div>
                    <div>2</div>
                    <div>3</div>
                    <div>4</div>
                    <div>5</div>
                    <div>▶</div>
                </div>

            </section>
        </>
    );
}

export default BoardList;
