import React from 'react'
import { useNavigate } from 'react-router-dom';
import { useRef, useState } from 'react';
function Write() {
    const refName = useRef();
    const refTitle = useRef();
    const refContent = useRef();
    const refPw = useRef();
    const nav = useNavigate();

    function chkSubmit(){
        const name = refName.current.value;
        const title = refTitle.current.value;
        const content = refContent.current.value;
        const pw = refPw.current.value;
    }

    return (
        <>
            <section className="notice">
                <div className="page-title">
                    <div className="container">
                        <h3>글쓰기</h3>
                    </div>
                </div>
                <div className="container">
                    <table class="table">
                        <tr>
                            <td>작성자</td>
                            <td>
                                <input type="text" name="name" size="30" ref={refName} /> 
                                <h6 Class='mb' style={{color:"red"}}>&nbsp;제목을 입력하세요.</h6>
                            </td>
                        </tr>

                        <tr>
                            <td>제목</td>
                            <td><input type="text" name="title" size="100" ref={refTitle} /></td>
                        </tr>

                        <tr>
                            <td>내용</td>
                            <td><textarea name="content" cols="100" rows="20" ref={refContent}></textarea></td>
                        </tr>

                        <tr>
                            <td>비밀번호</td>
                            <td><input type="password" name="pw" size="10" ref={refPw} /></td>
                        </tr>
                    </table>

                    <div className="foot_area">
                        <input type="button"  className="btn btn-dark reg"  value="작성" />
                        <input type="button" onClick={()=>nav(-1)}  className="btn btn-dark back"  value="뒤로가기" />
                    </div>

                </div >
            </section >
        </>
    )
}

export default Write;
